    "os"
)

func main() {
    counts := make(map[string]int) // like python dict {string: int}
    input := bufio.NewScanner(os.Stdin)
    for input.Scan() { //until EOF (ctrl + d)
        counts[input.Text()]++
    }
    for line, n := range counts {
