    fmt.Printf("%v", Scale(p, 2))
    fmt.Printf("%v", ScaleByPointer(&p, 2))
}

// 结构体作为函数参数
func Scale(p Point, factor int) Point {
    return Point{p.X * factor, p.Y * factor}
}

// 结构体作为函数参数
func ScaleByPointer(p *Point, factor int) Point {
    return Point{p.X * factor, p.Y * factor}
}
