    print('f')

registry = set()  # <1>

def register(active=True):  # <2>    工厂函数
    def decorate(func):  # <3>
        print('running register(active=%s)->decorate(%s)'
              % (active, func))
        else:
            registry.discard(func)  # <5>

        return func  # <6>
    return decorate  # <7>

@register(active=False)  # <8>
def f1():
    print('running f1()')

@register()  # <9>    # 即使没有参数，工厂函数必须写成调用的形式
def f2():
    print('running f2()')

class register(object):
    registry = set()

    def __init__(self, active=True):
        self.active = active

    def __call__(self, func):
        print('running register(active=%s)->decorate(%s)'
            % (self.active, func))
        if self.active:
            self.registry.add(func)
        else:
            self.registry.discard(func)

        return func

def func(l=None):    # 不要写 def func(l=[]):
    # 使用 None 作为占位符（pylint 默认会提示可变类型参数作为默认值，所以俺经常安利用 pylint 检测代码，防范风险）
